/*This snippet returns true if the predicate function returns true for all elements 
in a collection and false otherwise. You can omit the second argument fnif you want 
to use Booleanas a default value.*/
const all = (arr, fn = Boolean) => arr.every(fn);
/*
all([4, 2, 3], x => x > 1); // true
all([1, 2, 3]); // true
*/

/*This snippet checks whether all elements of the array are equal.*/
const allEqual = arr => arr.every(val => val === arr[0]);
/*
allEqual([1, 2, 3, 4, 5, 6]); // false
allEqual([1, 1, 1, 1]); // true
*/

/*This snippet checks whether two numbers are approximately equal to each other, with a small difference.*/
const approximatelyEqual = (v1, v2, epsilon = 0.001) => Math.abs(v1 - v2) < epsilon;
/*
approximatelyEqual(Math.PI / 2.0, 1.5708); // true
*/

/*This snippet converts the elements that donâ€™t have commas or double quotes to strings with comma-separated values.*/
const arrayToCSV = (arr, delimiter = ',') =>
  arr.map(v => v.map(x => `"${x}"`).join(delimiter)).join('\n');
/*
arrayToCSV([['a', 'b'], ['c', 'd']]); // '"a","b"\n"c","d"'
arrayToCSV([['a', 'b'], ['c', 'd']], ';'); // '"a";"b"\n"c";"d"'
*/

/*This snippet returns the average of two or more numerical values.*/
const average = (...nums) => nums.reduce((acc, val) => acc + val, 0) / nums.length;
/*
average(...[1, 2, 3]); // 2
average(1, 2, 3); // 2
*/

/*This snippet returns the average of an array after initially doing the mapping of
each element to a value using a given function.*/
const averageBy = (arr, fn) =>
  arr.map(typeof fn === 'function' ? fn : val => val[fn]).reduce((acc, val) => acc + val, 0) /
  arr.length;
/*
averageBy([{ n: 4 }, { n: 2 }, { n: 8 }, { n: 6 }], o => o.n); // 5
averageBy([{ n: 4 }, { n: 2 }, { n: 8 }, { n: 6 }], 'n'); // 5
*/

/*This snippet splits values into two groups and then puts a truthy element of filterin the first group, 
and in the second group otherwise.
You can use Array.prototype.reduce()and Array.prototype.push()to add elements to groups based on filter.*/
const bifurcate = (arr, filter) =>
  arr.reduce((acc, val, i) => (acc[filter[i] ? 0 : 1].push(val), acc), [[], []]);
/*  
bifurcate(['beep', 'boop', 'foo', 'bar'], [true, true, false, true]); 
// [ ['beep', 'boop', 'bar'], ['foo'] ]
*/

/*This snippet splits values into two groups, based on a predicate function. If the predicate function returns 
a truthy value, the element will be placed in the first group. Otherwise, it will be placed in the second group.
You can use Array.prototype.reduce()and Array.prototype.push()to add elements to groups, 
based on the value returned by fnfor each element.*/
const bifurcateBy = (arr, fn) =>
  arr.reduce((acc, val, i) => (acc[fn(val, i) ? 0 : 1].push(val), acc), [[], []]);
/*
bifurcateBy(['beep', 'boop', 'foo', 'bar'], x => x[0] === 'b'); 
// [ ['beep', 'boop', 'bar'], ['foo'] ]
*/

